import React, { useEffect, useState } from 'react'
import UserTable from './UserTable'
import AddUserForm from './AddUserForm'
import EditUserForm from './EditUserForm'
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from 'axios'

const api = axios.create({
    baseURL: `https://5d69d9fd6b97ef00145b7643.mockapi.io/api`
})
const App = () => {
    const userData = [
        { id: 1, name: 'Tangia', email: 'tangiablalba' },
        { id: 2, name: 'Tong NgocAnh', email: 'anh.tn184040' },
        { id: 3, name: 'Vu Thi Mai Nguyet', email: 'lele' },

    ]
    // const [data,setData]=useState([]);
    // useEffect( async ()=>{
    //     const res=await api.get("./users")
    //     setData(res)
    // },[])
    const [users, setUsers] = useState([])
    useEffect(() => {
        async function fetchData() {
            const res = await api.get("/users")
            setUsers(res.data)
        }
        fetchData()
    }, [])

    const [editing, setEditing] = useState(false)
    const intialFormState = { id: '', name: '', email: '', phone: '' }
    // Mang trang thai hien tai??
    const [currentUser, setCurrentUser] = useState(intialFormState)
    // Cai nay cho chuc nang edit
    const editRow = (user) => {
        setEditing(true)
        setCurrentUser({ id: user.id, name: user.name, email: user.email, phone: user.phone })
    }
    // const [users, setUsers] = useState(data)
    const addUser = (user) => {
        user.id = users.length + 1
        setUsers([...users, user])
    }
    const deleteUser = (id) => {
        setUsers(users.filter((user) => user.id !== id))
        // User duyet lai mang sao cho khong co phan tu id now. Tao ra mang moi. Well 1 cach de xoa phan tu.
    }
    const updateUser = (id, updateUser) => {
        setEditing(false)
        setUsers(users.map((user) => (user.id === id ? updateUser : user)))
    }
    // const handleRowUpdate=(newData,oldData,resolve)=>{
    //     let errorList=[]
    //     if(newData.name=='')
    //     {
    //         errorList.push('Please enter name')
    //     }
    //     if(newData.phone=='')
    //     {
    //         errorList.push('Please enter phone number')
    //     }
    //     if(newData.email=='')
    //     {
    //         errorList.push('Please enter valid email')
    //     }
    //     if(errorList.length<1)
    //     {
    //         api.put("/users/"+newData.id,newData)
    //     }
    // }
    return (
        <div className="container parrent">
            <h1 className="text-center">CRUD App with Hooks</h1>
            <div className="flex-row">
                <div className="flex-sm">
                    {editing ? (
                        //Return true ->  cho phep edit 
                        <div>
                            <h2>Edit user</h2>
                            <EditUserForm setEditing={setEditing} currentUser={currentUser} updateUser={updateUser} />
                        </div>
                    ) : (
                            <div>
                                <h2>
                                    Add user
                            </h2>
                                <AddUserForm addUser={addUser} />
                            </div>
                        )
                    }
                </div>
                <div className="flex-large">
                    <h2>View users</h2>
                    {/* Phan bang mac dinh */}
                    <UserTable users={users} deleteUser={deleteUser} editRow={editRow} />
                    {/* users o day chinh la mang cac phan tu hien tai.
                        deleteUser, editUSer la cac ham se can chay tuong

                    */}
                </div>

            </div>

        </div >
    )
}

export default App
