  // const handleInputChange = (event) => {
  //   const { name, value } = event.target

  //   setUser({ ...user, [name]: value })
  // }
  // useEffect(() => {
  //   setUser(props.currentUser)
  // }, [props])
  // return (
  //   <form
  //     onSubmit={(event) => {
  //       event.preventDefault()

  //       props.updateUser(user.id, user)
  //     }}
  //   >
  //     <label>Name</label>
  //     <input
  //       type="text"
  //       name="name"
  //       value={user.name}
  //       onChange={handleInputChange}
  //     />
  //     <label>email</label>
  //     <input
  //       type="text"
  //       name="email"
  //       value={user.email}
  //       onChange={handleInputChange}
  //     />
  //     <label>phone</label>
  //     <input
  //       type="text"
  //       name="phone"
  //       value={user.phone}
  //       onChange={handleInputChange}
  //     />
  //     <button>Update user</button>
  //     <button
  //       onClick={() => props.setEditing(false)}
  //       className="button muted-button"
  //     >
  //       Cancel
  //     </button>
  //   </form>
  // )
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const intialFormState={id:null,name:'',email:''}
  const [user,setUser]=useState(intialFormState)
  const handleInputChange=(event)=>
  {
      const {name,value}=event.target
      console.log(event.target)
      setUser({...user,[name]:value})
  }
  const onSubmit= (event)=>{
      event.preventDefault()
      if(!user.name || !user.email) return
      props.addUser(user)
      setUser(intialFormState)        
      setShow(false)
  }


  return (
      <>
          <Button variant="primary" onClick={handleShow}>
              Add
          </Button>

          <Modal show={show} onHide={handleClose} backdrop="static" >
              <Modal.Header closeButton>
                  <Modal.Title>Modal heading</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                  <InputGroup>
                      <InputGroup.Prepend>
                          <InputGroup.Text id="inputGroup-sizing-sm">Name</InputGroup.Text>
                      </InputGroup.Prepend>
                      <input type="text" name="name" value={user.name} onChange={handleInputChange}></input>
                  </InputGroup>
                  <InputGroup>
                      <InputGroup.Prepend>
                          <InputGroup.Text id="inputGroup-sizing-sm">Email</InputGroup.Text>
                      </InputGroup.Prepend>
                      <input type="text" name="email" value={user.email} onChange={handleInputChange}></input>
                  </InputGroup>
                  <InputGroup>
                      <InputGroup.Prepend>
                          <InputGroup.Text id="inputGroup-sizing-sm">Phone</InputGroup.Text>
                      </InputGroup.Prepend>
                      <input type="text" name="phone" value={user.phone} onChange={handleInputChange}></input>
                  </InputGroup>
              </Modal.Body>
              <Modal.Footer>
                  <Button variant="secondary" onClick={handleClose}>
                      Close
          </Button>
                  <Button variant="primary" onClick={onSubmit}>
                      Add
          </Button>
              </Modal.Footer>
          </Modal>
      </>
  );
}

export default EditUserForm
